<?phpnamespace App\Forms;use App\Entity\City;use App\Entity\Company;use Symfony\Bridge\Doctrine\Form\Type\EntityType;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\SubmitType;use Symfony\Component\Form\Extension\Core\Type\TextareaType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class AddCompany extends AbstractType{    public function buildForm(FormBuilderInterface $builder, array $options){        $builder            ->add('name', TextType::class,[                'required' => true,                'attr' => [                    'class' => 'form-control'                ],            ])            ->add('description', TextareaType::class,[                'attr' => [                    'class' => 'form-control'                ]            ])            ->add("address", TextType::class, [                'attr' => [                    'class' =>'form-control'                ]            ])            ->add("phone", TextType::class, [                'attr' => [                    'class' => 'form-control'                ]            ])            ->add("submit", SubmitType::class, [                'label' => 'Запази',                'attr' => [                    'class' => 'btn btn-primary'                ]            ])            ->add("city", EntityType::class,[                'class' => City::class,                'choice_label' => 'name',                'attr' =>[                    'class' => 'form-control selectpicker',                    'data-show-subtext' => "true",                    'data-live-search' => "true"                ]            ]);    }    public function configureOptions(OptionsResolver $resolver)    {        $resolver->setDefaults([            'data_class' => Company::class,        ]);    }    public function getName()    {        return 'company_add';    }}