<?phpnamespace App\Forms;use App\Entity\Account;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\EmailType;use Symfony\Component\Form\Extension\Core\Type\PasswordType;use Symfony\Component\Form\Extension\Core\Type\SubmitType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class EditProfile extends AbstractType{    public function buildForm(FormBuilderInterface $builder, array $options)    {        $builder            ->add('email', EmailType::class, [                'required' =>true,                'attr' => [                    'class' => 'form-control'                ]            ])            ->add('name', TextType::class, [                'attr' => [                    'class' => 'form-control'                ]            ])            ->add('new_password', PasswordType::class, [                'required' => false,                'mapped' => false,                'attr' => [                    'class' => 'form-control'                ]            ])            ->add('password', PasswordType::class, [                'required' => true,                'mapped' => false,                'attr' => [                    'class' => 'form-control'                ]            ])            ->add("submit", SubmitType::class, [                'label' => 'Обнови данните',                'attr' => [                    'class' => 'btn btn-primary'                ]            ]);    }    public function configureOptions(OptionsResolver $resolver)    {        $resolver->setDefaults([            'data_class' => Account::class,        ]);    }    public function getName()    {        return 'edit_profile_form';    }}